	/**
	 * Load content inside modal
	 */
	modal: function(_this)
	{
		var identifier								= Date.now();
		
		/* it will open the bootstrap 4 modal */
		$(
			'<div class="modal" id="dynamic-modal-' + identifier +'" role="dialog" aria-labelledby="dynamic-modal-title" aria-hidden="true">' +
				'<div class="modal-dialog modal-dialog-centered" role="document">' +
					'<div class="modal-content">' +
						'<div class="modal-header">' +
							'<h5 class="modal-title" id="dynamic-modal-title">' +
								'<i class="mdi mdi-loading mdi-spin"></i>' +
								'&nbsp;' +
								'<span class="modal-title-text">' +
									phrase.loading +
								'</span>' +
							'</h5>' +
							'<button type="button" class="close" data-dismiss="modal" aria-label="' + phrase.close + '">' +
								'<span aria-hidden="true">&times;</span>' +
							'</button>' +
						'</div>' +
						'<div class="modal-body p-0">' +
							'<div class="d-flex justify-content-center pt-5 pb-5">' +
								'<div class="spinner-border" role="status">' +
									'<span class="sr-only">' + phrase.loading + '</span>' +
								'</div>' +
							'</div>' +
						'</div>' +
					'</div>' +
				'</div>' +
			'</div>'
		)
		.appendTo('body')
		.modal({backdrop:'static', keyboard: false}),
		$.ajax
		({
			url: _this.attr('href'),
			context: this,
			method: 'POST',
			data:
			{
				prefer: 'modal'
			}
		})
		.done(function(response)
		{
			if(response)
			{
				if(301 === response.status || 403 === response.status || 404 === response.status)
				{
					if(response.redirect)
					{
						return window.location.replace(response.target);
					}
					$('.modal').modal('hide');
					return throw_exception(response.status, response.exception);
				}
				$('#dynamic-modal-' + identifier).find('.modal-body').html(response.html),
				(response && response.meta && response.meta.modal_size ? $('.modal-dialog').addClass(response.meta.modal_size) : null),
				(!_this.hasClass('--prevent-remove') ? _reactivate() : null)
			}
			else
			{
				$('#dynamic-modal-' + identifier).find('.modal-body').html
				(
					'<div class="container-fluid">' +
						'<div class="row bg-dark full-height">' +
							'<div class="col-12">' +
								'<div class="pt-3 pb-3 text-success text-monospace">' +
									'<p class="mb-0">' +
										'[info@localhost ~]# aksara --tell -readonly' +
									'</p>' +
									'<p class="mb-0 text-danger">' +
										phrase.no_view_could_be_loaded +
										'<br />' +
										phrase.make_sure_to_check_the_following_mistake + ':' +
										'<ol class="text-danger">' +
											'<li>' +
												phrase.module_structure + ',' +
											'</li>' +
											'<li>' +
												phrase.incorect_view_path + ',' +
											'</li>' +
											'<li>' +
												phrase.database_table_existence + ',' +
											'</li>' +
											'<li>' +
												phrase.something_caused_by_typo + '.' +
											'</li>' +
										'</ol>' +
									'</p>' +
									'<p class="mb-0">' +
										'[info@localhost ~]# <blink>_</blink>' +
									'</p>' +
								'</div>' +
							'</div>' +
						'</div>' +
					'</div>'
				)
			}
			$('#dynamic-modal-' + identifier).find('.modal-title').find('.mdi').removeClass('mdi mdi-loading mdi-spin').addClass((response && response.meta ? response.meta.icon : 'mdi mdi-alert-outline')),
			$('#dynamic-modal-' + identifier).find('.modal-title').find('.modal-title-text').text((response && response.meta ? response.meta.title : phrase.title_was_not_set))
		})
	},
	
